  // // trying to render images but im not sure how yet?
  // let filterImage = mapImage.filter(renderImages => 
  //   renderImages);
  //   console.log('filterImage ', filterImage)

  return(
    <div className = 'list-container'>
      {optInStaffMembers.map(renderTheList => (
        <li
          key = {renderTheList.fields.Name}
          className = 'list'>
            {renderTheList.fields.Name.toUpperCase()}
          </li>
      ))}
    </div>
  )
}

  export default DirectoryList;
