import React, { Component } from "react";
import { compose } from "recompose";
import {
  AuthUserContext,
  withAuthorization,
  withEmailVerification
} from "../../components/Session";
import { withFirebase } from "../../components/Firebase";
import { messaging } from "firebase";
const options = ["Select Tag", "React", "Ruby", "Javascript"];
const moment = require("moment");

class Dialog extends React.Component {
  render() {
    return (
      <div className="modal-wrapper-postarticle">
      <div className="dialogstyle devedit-form">
        <button
          className=" dialogCloseButonStayle"
          onClick={this.props.closePopup}
        >
          X
        </button>
        <div className="diglog_inner">
          <h4>{this.props.children}</h4>
        </div>
      </div>
      </div>
    );
  }
}
class Createarticle extends Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "",
      description: "",
      tags: "Select an Option",
      url: "",
      downvotes: 0,
      upvotes: 0,
      authorID: "",
      timeCreated: "",
      userName:""

    };

  }

  togglePopup=()=> {

    this.setState({
      showPopup: !this.state.showPopup

    });

  }

  onUrlChange = e => {
    this.setState({
      url: e.target.value
    });
  };
  onTagChange = e => {
    this.setState({
      tags: e.target.value
    });
  };
  onTitleChange = e => {
    this.setState({
      title: e.target.value
    });
  };
  onBodyChange = e => {
    this.setState({
      description: e.target.value
    });
  };

  handleSubmit = (e, authUser) => {
    e.preventDefault();
    this.props.firebase
      .articles()
      .add({
        userId: authUser.uid,
       // userName:this.state.userName,
        title: this.state.title,
        description: this.state.description,
        tags: this.state.tags,
        url: this.state.url,
        downvotes: this.state.downvotes,
        upvotes: this.state.upvotes,
        timeCreated: moment().format(` MMMM DD, YYYY  --  hh:mm:ss A  UTC-6`)
      })
      .then(docRef => {

        this.setState({confurmmessage:docRef.id})
        console.log("Document written with ID: ", this.state.confurmmessage);
     alert("you've successfully created an article with ID: " + this.state.confurmmessage);

      });
    this.setState({
      tags: "",
      title: "",
      url: "",
      description: "",
      downvotes: 0,
      upvotes: 0,
      showPopup:false
    });

  }
  render() {
    return (
      <AuthUserContext.Consumer>
        {authUser => (
          <div>
            <button className="button-secondary1"
              onClick={this.togglePopup}>
              Post New Article
              </button>  
            {this.state.showPopup ? (
              <Dialog closePopup={this.togglePopup}>

                <div >
                  <div >
                   {/* <div style={{fontSize:"legend"}}>Create new post</div>*/} 
                    <legend className="devedit-form-legend1">Create New Post</legend>
                    <div >
                      <div >
                        <ul>
                          <li>
                            <form

                              onSubmit={e => this.handleSubmit(e, authUser)}

                            >
                              <ul>
                              <li>
                                  <input
                                    type="text"
                                    placeholder="Title"
                                    value={this.state.title}
                                    onChange={this.onTitleChange}
                                    required
                                  />
                                </li>
                                <li>
                                  <select
                                    value={this.state.tags}
                                    onChange={this.onTagChange}
                                  >
                                    {options.map(option => {
                                      return (
                                        <option value={option} key={option}>
                                          {option}
                                        </option>
                                      );
                                    })}
                                  </select>
                                </li>

                                <li>
                                <input
                                    type="text"
                                    placeholder="URl"
                                    value={this.state.url}
                                    onChange={this.onUrlChange}
                                    required
                                  />
                                </li>
                                <li>
                                  <textarea
                                    col={30}
                                    rows={10}
                                    placeholder="Description"
                                    value={this.state.description}
                                    onChange={this.onBodyChange}
                                    required

                                  />

                                </li>
                                <li>

                                  <button className="button-tertiary" type="submit" onClick={this.closeSelf}> Post</button>
                                </li>

                              </ul>
                            </form>
                          </li>
                        </ul>
                      </div>
                    </div>
                  </div>
                </div>
              </Dialog>
            ) : null}
          </div>
        )}
      </AuthUserContext.Consumer>
    );
  }
}

export default compose(withFirebase)(Createarticle);
